var someBlock = $('.main-content-inner');
function preloaderRemove() {
  someBlock.preloader('remove');
}
function app_menu(type) {
      switch (type) {
        case 'Dashboards':
                someBlock.preloader();
                $(".main-content-inner").load("Dashboards/Dashboards",function(response, status, jqXHR){
                  if(status == "success"){
                      preloaderRemove();
                  }
                });
        break;
        case 'Profiles':// Thông Tin Cá Nhân
                  someBlock.preloader();
                  $(".main-content-inner").load("Profiles/Profiles",function(response, status, jqXHR){
                    if(status == "success"){
                        preloaderRemove();
                    }
                  });
        break;


        }
}
